graphql.query:
mutation ($payload:{{ toPascalCase schema.boundedContextName }}Create{{ toPascalCase schema.moduleName }}Input!)
{
	{{ toCamelCase schema.boundedContextName }}Create{{ toPascalCase schema.moduleName }} (payload:$payload)
	{{ bracketOpen }}{{#each schema.properties.postmanGraphQLCreateQuery}}
		{{ toCamelCase name }}{{/each}}
	}
}

graphql.variables:
{
	"payload":
	{{ bracketOpen }}{{#each schema.properties.postmanGraphQLCreateVariables}}
		"{{ toCamelCase name }}": {{{ postmanQuotes . }}}{{{ fakerPostman . }}}{{{ postmanQuotes . }}}{{#unless @last}}, {{/unless}}{{/each}}
	}
}
